INSERT product (product_cat, product_name, product_price, product_image, description1, description2, description3, product_dimensions) VALUES (0, 'Arctic Hat', 24.95, 'Arctic_Hat.png', 'Arctic Hat is the new hat that combines controlled evaporative cooling and UV protection from the sun?s visible and invisible rays. Arctic Hats evaporative cooling inner liner retains cool water and slowly evaporates to keep your head up 20&deg;F cooler. Simply wet the inner lining and put on the hat! Get instant relief from the heat and stay cool for hours.', 'Arctic Hat also has a built-in radiant barrier to deflect and block 99% of the sun?s harmful UV rays and reflects 80% of the suns heat. Arctic Hat is made from a durable, breathable and lightweight construction. The same technology is used by professional athletes, the US military, and emergency services. Best of all, Arctic Hat is adjustable, so one size fits most! Perfect for the whole family!', 'null', 'null');
