INSERT product (product_cat, product_name, product_price, product_image, description1, description2, description3, product_dimensions) VALUES (3, 'Lizard Cam', 49.99, 'Lizard_Cam.jpg', 'Lizard Cam by Atomic Beam is the flexible, micro-inspection camera that snakes into tight, hard-to-reach spaces. It?s easy to use just feed and view. The 4-foot long cable bends and flexes to fit into your desired space. At the end of the waterproof cable is a micro camera with a built-in LED light that sees all. View everything that the camera captures on the hand-held view screen. Use the snake camera beyond pipes and clogs. Attach the magnetic tip to the end of the cable and easily pick up screws, nails or other hardware you?ve dropped but can?t see. Look behind or underneath appliances without having to move them. Retrieve lost cat toys from under the refrigerator with the handy hook tip. Lizard Cam is a must-have tool.', 'null', 'null', 'null');
