INSERT product (product_cat, product_name, product_price, product_image, description1, description2, description3, product_dimensions) VALUES (4, '3 Second Brow', 24.99, '3_Second.jpg', 'The 3 Second Brow eyebrow stamp is the perfect, easy way to natural-looking eyebrows in second! As seen on TV 3 Second Brows gives you perfectly symmetrical eyebrow in seconds. Whether you want to shape and define for fuller brows or fill in sparse eyebrow its easy with three second brow. Gently press stamp into the brow powder, place stamp on top of eyebrow and enjoy the beautiful brow you always dreamed of.', null, null, null);
